#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/echo$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/echo$exe" off & "$basedir/../@squarespace/server/build/distributions/local-developer/bin/run.bat" $args
  } else {
    & "$basedir/echo$exe" off & "$basedir/../@squarespace/server/build/distributions/local-developer/bin/run.bat" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "echo$exe" off & "$basedir/../@squarespace/server/build/distributions/local-developer/bin/run.bat" $args
  } else {
    & "echo$exe" off & "$basedir/../@squarespace/server/build/distributions/local-developer/bin/run.bat" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
